const animations = editor.interface.animation.settings.templates
const current_template =  animations[0];
const opts = current_template.opts;

let count_0 = 0;
current_template.animations.move.group.forEach((ind)=>{
if( ind !== 10  ){
 count_0+=1;
  editor.animator.move({
  x:[0,current_template.animations.move.axis[0]],
  y:[0,current_template.animations.move.axis[1]],
  t:[0,1].map(s=>s/opts.speedOfMove.value),
  st:count_0 * opts.staggeringMove.value + opts.moveStartTime.value +current_template.templateStartTime,
  ease:current_template.animations.move.ease,
  i:0},ind);
  }
})
let count_1 = 0;

current_template.animations.fadeIn.group.forEach((ind)=>{
count_1 +=1;
template.layers[ind]._opacity = 0;
editor.animator.fade({
  op:[1],
  t:[0,1].map(s=>s/opts.speedOfFadeIN.value),
  st: count_1 * opts.staggeringFadein.value + opts.fadeInStartTime.value +current_template.templateStartTime,
  ease:current_template.animations.fadeIn.ease,i:0},ind);
})

